query Products ($handle: String! ) {
    productByHandle(handle: $handle) {
        id
        title
        variants(first:20) {
            edges {
                node {
                    id
                    title
                    sku
                    price 
                    inventoryItem {
                        id
                    }
                }
            }
        }
        updatedAt
        createdAt
    }
}

mutation productCreate($product: ProductCreateInput!, $media: [CreateMediaInput!]) {
  productCreate(product: $product, media: $media) {
    product {
     id
      title
      descriptionHtml
      vendor
      productType
      status
      handle
      variants(first:10) {
        edges {
            node {
                id
                sku
                displayName
                inventoryItem {
                    id
                    sku
                    tracked
                    requiresShipping
                }
            }
        }
      }
      media(first: 10) {
        edges {
            node {
                alt
                mediaContentType
                status
                id
                preview {
                    image {
                        url
                    }
                    status
                }
            }
        }
      }
      options {
        id
        name
        position
        optionValues {
          id
          name
          hasVariants
        }
      }
    }
    userErrors {
      field
      message
    }
  }
}

